    //上传图片
	$scope.uploadImage=function(result){

		var tempFilePaths;
		var result = result;
		wx.chooseImage({
			  count: 1, // 默认9
			  sizeType: ['compressed'], // 可以指定是原图还是压缩图，默认二者都有
			  sourceType: ['album', 'camera'], // 可以指定来源是相册还是相机，默认二者都有
			  success: function (res) {
			    // 返回选定照片的本地文件路径列表，tempFilePath可以作为img标签的src属性显示图片
			    tempFilePaths = res.localIds[0];
			    // ngUtils.alert(tempFilePaths);
                var localData;
			    wx.getLocalImgData({
					localId: tempFilePaths, // 图片的localID
					success: function (res) {
				        localData = res.localData; // localData是图片的base64数据，可以用img标签显示
				        // ngUtils.alert(localData);
                        if(result=='1') {
                        	$scope.imgSrc1 = 'data:image/png;base64,'+localData;
				        	$scope.isUplod1 = true;
		                    $('.fydFeedback').trigger('click')
						} else if(result=='2') {
							$scope.imgSrc2 =  'data:image/png;base64,'+localData;
				            $scope.isUplod2 = true;
				            $('.fydFeedback').trigger('click')
						} else if(result=='3') {
						    $scope.imgSrc3 =  'data:image/png;base64,'+localData;
				            $scope.isUplod3 = true;	
				            $('.fydFeedback').trigger('click')
						}				
					}
				});	
				
			
			  },
			  fail: function (res) {
			  	ngUtils.alert(JSON.stringify(res));
			  	
			  },
        })	        
	}	
